import React, { useState } from 'react';
// import Home from './Home';
import { PDFViewer, Page, Document, StyleSheet, BlobProvider } from '@react-pdf/renderer';
import { saveAs } from 'file-saver';

const styles = StyleSheet.create({
  page: {
    width: '200mm', 
    height: '300mm', 
    padding: '20mm',
  },
});

const ReactPdf = () => {
  const [pdfContent, setPdfContent] = useState(['Page content']); 

  const handleCopyPage = (numCopies) => {
    const copiedContent = [...pdfContent];
    for (let i = 0; i < numCopies; i++) {
      copiedContent.push('Copied content'); 
    }
    setPdfContent(copiedContent);
  };

  const handleGeneratePDF = () => {
    const MyDoc = () => (
      <Document>
        {pdfContent.map((content, index) => (
          <Page key={index} style={styles.page}>
            <p>{content}</p>
          </Page>
        ))}
      </Document>
    );

    return (
      <BlobProvider document={<MyDoc />}>
        {({ blob }) => {
          saveAs(blob, 'customized-pdf.pdf');
        }}
      </BlobProvider>
    );
  };

  return (
    <div>
      <button onClick={() => handleCopyPage(3)}>Copy Page 3 times</button>
      <button onClick={handleGeneratePDF}>Download PDF</button>
      <PDFViewer>
        {/* <MyDoc></MyDoc> */}
      </PDFViewer>
    </div>
  );
};

export default ReactPdf;
