import React, { useRef } from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

const App = () => {
  const contentRef = useRef(null);

  const handleDownloadPDF = async () => {
    const pdf = new jsPDF('p', 'mm', 'a4'); // Create a new PDF document

    const canvas = await html2canvas(contentRef.current); // Capture the content as a canvas

    const imgData = canvas.toDataURL('image/png'); // Convert canvas to image data

    pdf.addImage(imgData, 'PNG', 15, 15, 180, 0); // Add the image to the PDF

    pdf.save('webpage_content.pdf'); // Save the generated PDF with the given filename
  };

  return (
    <div>
      <h1>Webpage Content Downloader</h1>
      <div ref={contentRef}>
        {/* Add your webpage content here */}
        <p>This is the content of your webpage.</p>
        <p>You can put any other HTML content here.</p>
      </div>
      <button onClick={handleDownloadPDF}>Download PDF</button>
    </div>
  );
};

export default App;
