import React, {useEffect, useRef, useState} from 'react';
import Home from './Home';
import './ReactToPdf.css'
import { useReactToPrint } from 'react-to-print';


const ReactPdfPrint = () => {
    const[tootTipContent, settoolTipContent] = useState();
    const[isShown, setIsShown] = useState();
    useEffect(() => {
        fetchToolKit();
    },[]
    )

    const fetchToolKit = async () => {
        try{
            const resData = await fetch('http://localhost:5000/api/tooltip');
            const data = await resData.json();
            settoolTipContent(data.message)
        }
        catch(error){
            console.log('Can not load message', error);
        }
    }
    const componentRef = useRef();
    const handlePrint = useReactToPrint({
        content : () => componentRef.current,
        documentTitle : 'to_pdf',
    });

    return (
        <> 
            <div ref = {componentRef} style = {{width : '100%', height : window.innerHeight}}>
                <Home></Home>
                <button className = 'button_plus' onClick={handlePrint} onMouseEnter={() => setIsShown(true)} onMouseLeave={() => setIsShown(false)}>
                    `{isShown && <span>{}</span>}``
                </button>
            </div>
        </>
    )
}

export default ReactPdfPrint;
