import React from 'react';
import { PDFDownloadLink, Document, Page, Text, View, Image, StyleSheet } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#ffffff',
    padding: 10,
  },
  heading: {
    fontSize: 20,
    marginBottom: 10,
  },
  paragraph: {
    fontSize: 12,
    marginBottom: 5,
  },
  image: {
    width: '100%',
    marginBottom: 10,
  },
});

const WebpageToPDF = () => {
  return (
    <div>
      <h1>Webpage to PDF Downloader</h1>
      <Document>
        <Page style={styles.page}>
          <View>
            {/* Replace this with your webpage content */}
            <Text style={styles.heading}>Welcome to My Webpage</Text>
            <Text style={styles.paragraph}>This is a sample content.</Text>
            <Image src="https://example.com/sample-image.jpg" style={styles.image} />
            {/* End of your webpage content */}
          </View>
        </Page>
      </Document>
      <PDFDownloadLink document={<Document>
        <Page style={styles.page}>
          <View>
            {/* Replace this with your webpage content */}
            <Text style={styles.heading}>Welcome to My Webpage</Text>
            <Text style={styles.paragraph}>This is a sample content.</Text>
            <Image src="https://example.com/sample-image.jpg" style={styles.image} />
            {/* End of your webpage content */}
          </View>
        </Page>
      </Document>} fileName="webpage.pdf">
        {({ blob, url, loading, error }) => (loading ? 'Loading document...' : 'Download as PDF')}
      </PDFDownloadLink>
    </div>
  );
};

export default WebpageToPDF;
