import React from 'react';
import jsPDF from 'jspdf';
import puppeteer from 'puppeteer';

import Home from './Home';

const ReactPdf = () => {
  const handleDownloadPDF = async () => {
    const pdfWidth = 100; 
    const pdfHeight = 100; 

    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto(window.location.href, { waitUntil: 'networkidle0' });

    const contentWidth = await page.evaluate(() => document.documentElement.scrollWidth);
    const contentHeight = await page.evaluate(() => document.documentElement.scrollHeight);
    await page.setViewport({ width: pdf, height: contentHeight });

    const screenshot = await page.screenshot();

    await browser.close();

    const imageUrl = URL.createObjectURL(new Blob([screenshot]));


    const pdf = new jsPDF('p', 'mm', [pdfWidth, pdfHeight]);
    pdf.addImage(imageUrl, 'JPEG', 0, 0, pdfWidth, pdfHeight);

    pdf.save('webpage_content.pdf');
  };

  return (
    <div>
      <Home></Home>
      <button onClick={handleDownloadPDF}>Download PDF</button>
    </div>
  );
};

export default ReactPdf;
